import { useEffect, useState } from 'react';
import { chargeFilesData,chargeFiles } from './api/files';
import {useDispatch,useSelector} from 'react-redux';
import './App.css';
import { addFiles, addLines } from './redux/reducer';
import { PanelSelectFile } from './components/molecules/panelSelectFile/PanelSelectFile';
import { Header } from './components/atoms/header/Header';
import { TableLines } from './components/organisms/table/Table';

function App() {
  const [filt,setFilt]=useState('');
  const dispatch=useDispatch();
  const {files,lines}=useSelector(state=>state.filesReducer)
  useEffect(()=>{
    const chargeData=async()=>{
      const filesData=await chargeFilesData(filt);
      const files=await chargeFiles();
      Promise.all([filesData,files])
      .then(result=>{
        dispatch(addFiles(result[1]))
        dispatch(addLines(result[0]))
      })
    }
    chargeData();
  },[filt,dispatch])

  const select=(name)=>{
    setFilt(name)
  }

  return (
    <div>
      <Header/>
      <PanelSelectFile files={files} selected={filt} action={(name)=>select(name)} />
      <hr/>
      {
        !filt?
          <div>Selecciona un archivo</div>
          :
            lines.lenght?
            <div>este sirve</div>:
            <div>este no</div>
      }
    </div>
  );
}

export default App;
