import { useEffect } from 'react';
import { chargeFilesData,chargeFiles } from './api/files';
import {useDispatch,useSelector} from 'react-redux';
import './App.css';
import { addFiles, addLines } from './redux/reducer';
import { PanelSelectFile } from './components/molecules/panelSelectFile/PanelSelectFile';
import { Header } from './components/atoms/header/Header';
import { TableLines } from './components/organisms/table/Table';

function App() {
  const dispatch=useDispatch();
  const {files,lines,selected}=useSelector(state=>state.filesReducer)
  useEffect(()=>{
    const chargeData=async()=>{
      const filesData=await chargeFilesData(selected);
      const files=await chargeFiles();
      Promise.all([filesData,files])
      .then(result=>{
        if(selected){
          dispatch(addLines(result[0]))
        }
        dispatch(addFiles(result[1]))
      })
    }
    chargeData();
  },[selected,dispatch])
  useEffect(()=>{

    console.log(lines)
  },[lines])
  /*
  Valores que puede tomar el lines
  [] o 
  [
    {
      file:nombre,
      lines:[
        {text:asdsadasd,number:123124214,hex:asdasd12e},
        ...
      ]
    }
  ]
  */
  return (
    <div>
      <Header/>
      <PanelSelectFile files={files} />
      <hr className='no-space'/>
      {
        !selected?
          <div>Selecciona un archivo</div>
          :
          <TableLines data={lines}/>
      }
    </div>
  );
}

export default App;
