import { useEffect, useState } from 'react';
import { chargeFilesData,chargeFiles } from './api/files';
import {useDispatch,useSelector} from 'react-redux';
import './App.css';
import { addFiles, addLines } from './redux/reducer';

function App() {
  const [filt,setFilt]=useState('');
  const dispatch=useDispatch();
  const {files,lines}=useSelector(state=>state.filesReducer)
  useEffect(()=>{
    const chargeData=async()=>{
      const filesData=await chargeFilesData(filt);
      const files=await chargeFiles();
      Promise.all([filesData,files])
      .then(result=>{
        dispatch(addFiles(result[1]))
        dispatch(addLines(result[0]))
      })
    }
    chargeData();
  },[filt])

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
