import { useEffect, useState } from 'react';
import { chargeFilesData,chargeFiles } from './api/files';
import {useDispatch,useSelector} from 'react-redux';
import './App.css';
import { addFiles, addLines } from './redux/reducer';
import { PanelSelectFile } from './components/molecules/panelSelectFile/PanelSelectFile';

function App() {
  const [filt,setFilt]=useState('');
  const [selected,setSelected]=useState('')
  const dispatch=useDispatch();
  const {files,lines}=useSelector(state=>state.filesReducer)
  useEffect(()=>{
    const chargeData=async()=>{
      const filesData=await chargeFilesData(filt);
      const files=await chargeFiles();
      Promise.all([filesData,files])
      .then(result=>{
        dispatch(addFiles(result[1]))
        dispatch(addLines(result[0]))
      })
      setSelected(files[0]);
    }
    chargeData();
  },[filt])

  const select=(name)=>{
    setFilt(name)
  }

  return (
    <div>
      <PanelSelectFile files={files} selected={selected} action={(name)=>select(name)} />
    </div>
  );
}

export default App;
